// src/components/layout/Header.jsx
import { Menu, ChevronDown, Bell } from 'lucide-react';
import { useState } from 'react';
import { cn } from '../../lib/utils';

export default function Header({ onMenuClick }) {
  const [isUserMenuOpen, setIsUserMenuOpen] = useState(false);
  const [notificationCount, setNotificationCount] = useState(3); // Número de notificaciones pendientes

  return (
    <header className="bg-white border-b border-gray-200 h-16 flex items-center justify-between px-6">
      <button
        onClick={onMenuClick}
        className="p-2 rounded-lg hover:bg-gray-100 md:hidden"
      >
        <Menu className="h-6 w-6" />
      </button>

      <div className="ml-auto flex items-center space-x-6 relative">
        {/* Icono de notificaciones */}
        <div className="relative">
          <Bell className="h-6 w-6 text-gray-600 cursor-pointer" />
          {notificationCount > 0 && (
            <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full px-1">
              {notificationCount}
            </span>
          )}
        </div>

        {/* Menú de usuario */}
        <div>
          <button
            onClick={() => setIsUserMenuOpen(!isUserMenuOpen)}
            className="flex items-center space-x-2 hover:bg-gray-100 rounded-lg px-3 py-2"
          >
            <span>Bienvenido, Usuario</span>
            <ChevronDown className="h-5 w-5" />
          </button>

          {isUserMenuOpen && (
            <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg py-1">
              <a href="#" className="block px-4 py-2 hover:bg-gray-100">Mi Perfil</a>
              <a href="#" className="block px-4 py-2 hover:bg-gray-100">Configuración</a>
              <a href="#" className="block px-4 py-2 hover:bg-gray-100 text-red-600">Cerrar Sesión</a>
            </div>
          )}
        </div>
      </div>
    </header>
  );
}

// src/components/layout/Layout.jsx
import { useState } from 'react';
import Sidebar from './Sidebar';
import Header from './Header';

export default function Layout({ children }) {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <div className="flex h-screen bg-gray-50">
      <Sidebar isOpen={sidebarOpen} />
      <div className="flex-1 flex flex-col overflow-hidden">
        <Header onMenuClick={toggleSidebar} />
        <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50 p-6">
          {children}
        </main>
      </div>
    </div>
  );
}

// src/components/layout/Sidebar.jsx
import { Home, Users, Briefcase, Building2, Warehouse, DollarSign } from 'lucide-react';
import { cn } from '../../lib/utils';

const navigation = [
  { name: 'Dashboard', href: '#', icon: Home, current: true },
  { name: 'Administración', href: '#', icon: Briefcase, current: false },
  { name: 'Bodega', href: '#', icon: Warehouse, current: false },
  { name: 'Finanzas', href: '#', icon: DollarSign, current: false },
  { name: 'Obras', href: '#', icon: Building2, current: false },
  { name: 'Usuarios', href: '#', icon: Users, current: false },
];

export default function Sidebar({ isOpen }) {
  return (
    <aside className={cn(
      "fixed inset-y-0 left-0 z-50 w-64 bg-[#17a2b8] transform transition-transform duration-300 ease-in-out md:translate-x-0 md:static md:inset-auto",
      !isOpen && "-translate-x-full"
    )}>
      <div className="flex flex-col h-full">
        <div className="px-4 py-6">
          <h1 className="text-white text-xl font-bold">Constructora Pakar ERP</h1>
        </div>

        <nav className="mt-6 flex-1">
          {navigation.map((item) => (
            <a
              key={item.name}
              href={item.href}
              className={cn(
                "flex items-center px-6 py-3 text-white hover:bg-white/10 transition-colors",
                item.current && "bg-white/20"
              )}
            >
              <item.icon className="h-5 w-5 mr-3" />
              {item.name}
            </a>
          ))}
        </nav>
      </div>
    </aside>
  );
}

